import React, {useRef, useEffect, useState} from 'react'

export default function CameraCapture({ deviceId, onVerified }){
  const videoRef = useRef(null)
  const canvasRef = useRef(null)
  const [status,setStatus] = useState('idle')

  useEffect(()=>{
    let mounted = true
    async function start(){
      try{
        const s = await navigator.mediaDevices.getUserMedia({video:true})
        if(!mounted) return
        if(videoRef.current) videoRef.current.srcObject = s
      }catch(e){
        setStatus('error')
      }
    }
    start()
    return ()=>{
      mounted = false
      const s = videoRef.current && videoRef.current.srcObject
      if(s) s.getTracks().forEach(t=>t.stop())
    }
  },[])

  function capture(){
    setStatus('capturing')
    const v = videoRef.current
    const c = canvasRef.current
    if(!v||!c) return
    c.width = v.videoWidth || 640
    c.height = v.videoHeight || 480
    const ctx = c.getContext('2d')
    ctx.drawImage(v,0,0,c.width,c.height)
    c.toBlob(async (blob)=>{
      if (!blob) { setStatus('error'); return }
      setStatus('uploading')
      try {
        const fd = new FormData()
        fd.append('file', blob, 'selfie.jpg')
        const resp = await fetch(`http://localhost:8000/verify?device_id=${encodeURIComponent(deviceId)}`, {
          method: 'POST',
          body: fd,
        })
        const data = await resp.json()
        setStatus('verified')
        const nickname = localStorage.getItem('anon_nickname') || ''
        const bio = localStorage.getItem('anon_bio') || ''
        onVerified && onVerified({ gender: data.gender, nickname, bio })
      } catch (e) {
        console.error(e)
        setStatus('error')
      }
    },'image/jpeg')
  }

  return (
    <div>
      <div style={{width:360,height:270,background:'#000',borderRadius:8,overflow:'hidden'}}>
        <video ref={videoRef} autoPlay muted playsInline style={{width:'100%',height:'100%',objectFit:'cover'}} />
      </div>
      <div style={{marginTop:8}}>
        <button onClick={capture}>Capture (gallery disabled)</button>
      </div>
      <canvas ref={canvasRef} style={{display:'none'}} />
      <div>Status: {status}</div>
    </div>
  )
}
