import React, { useRef, useEffect, useState } from 'react'
import { getOrCreateDeviceId } from '../utils/fingerprint'

export default function CameraCapture({ deviceId, onVerified }) {
  const videoRef = useRef(null)
  const canvasRef = useRef(null)
  const [status, setStatus] = useState('idle')
  const [nickname, setNickname] = useState(localStorage.getItem('anon_nickname') || '')
  const [bio, setBio] = useState(localStorage.getItem('anon_bio') || '')
  const [showProfileForm, setShowProfileForm] = useState(!nickname)

  useEffect(() => {
    let mounted = true
    async function start() {
      try {
        const s = await navigator.mediaDevices.getUserMedia({ 
          video: { facingMode: 'user' },
          audio: false 
        })
        if (!mounted) return
        if (videoRef.current) videoRef.current.srcObject = s
        setStatus('ready')
      } catch (e) {
        console.error('Camera error:', e)
        setStatus('camera-error')
      }
    }
    start()
    return () => {
      mounted = false
      const s = videoRef.current && videoRef.current.srcObject
      if (s) s.getTracks().forEach(t => t.stop())
    }
  }, [])

  function saveProfile() {
    if (!nickname.trim()) {
      alert('Please enter a nickname')
      return
    }
    localStorage.setItem('anon_nickname', nickname)
    localStorage.setItem('anon_bio', bio)
    setShowProfileForm(false)
  }

  function capture() {
    if (showProfileForm) {
      saveProfile()
      return
    }
    setStatus('capturing')
    const v = videoRef.current
    const c = canvasRef.current
    if (!v || !c) return

    c.width = v.videoWidth || 640
    c.height = v.videoHeight || 480
    const ctx = c.getContext('2d')
    ctx.drawImage(v, 0, 0, c.width, c.height)

    c.toBlob(async (blob) => {
      if (!blob) {
        setStatus('error')
        return
      }
      setStatus('uploading')
      try {
        const fd = new FormData()
        fd.append('file', blob, 'selfie.jpg')
        const resp = await fetch(
          `http://localhost:8000/verify?device_id=${encodeURIComponent(deviceId)}`,
          {
            method: 'POST',
            body: fd,
          }
        )
        if (!resp.ok) throw new Error(`Server error: ${resp.status}`)
        const data = await resp.json()
        console.log('Verification result:', data)
        setStatus('verified')
        onVerified && onVerified({
          gender: data.gender,
          nickname: nickname.trim(),
          bio: bio.trim(),
        })
      } catch (e) {
        console.error('Upload error:', e)
        setStatus('error')
      }
    }, 'image/jpeg', 0.9)
  }

  return (
    <div className="card camera-box">
      <h2>Verification</h2>
      {status === 'camera-error' && (
        <div style={{ color: '#ff6b6b' }}>
          ‚ùå Camera permission denied. Please enable camera access.
        </div>
      )}
      {status !== 'verified' && status !== 'camera-error' && (
        <>
          <div className="video-wrap">
            {status === 'ready' || status === 'idle' || status === 'capturing' || status === 'uploading' ? (
              <video
                ref={videoRef}
                autoPlay
                muted
                playsInline
                style={{ width: '100%', height: '100%', objectFit: 'cover' }}
              />
            ) : (
              <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: '100%', color: '#9aa4b2' }}>
                {status === 'error' ? '‚ùå Error' : '‚úì Verified'}
              </div>
            )}
          </div>
          <canvas ref={canvasRef} style={{ display: 'none' }} />

          {showProfileForm && (
            <div className="profile-setup">
              <label>
                <div style={{ fontSize: 12, color: '#9aa4b2', marginBottom: 4 }}>Nickname</div>
                <input
                  type="text"
                  className="input"
                  value={nickname}
                  onChange={(e) => setNickname(e.target.value)}
                  placeholder="Your anon name"
                  maxLength={30}
                />
              </label>
              <label>
                <div style={{ fontSize: 12, color: '#9aa4b2', marginBottom: 4 }}>Bio</div>
                <textarea
                  className="input"
                  value={bio}
                  onChange={(e) => setBio(e.target.value)}
                  placeholder="Tell us about yourself (optional)"
                  maxLength={120}
                  rows={3}
                  style={{ resize: 'none' }}
                />
              </label>
            </div>
          )}

          <div className="status" style={{ fontSize: 12, textAlign: 'center', minHeight: 28, background: 'rgba(94, 234, 212, 0.1)', color: '#5eead4' }}>
            {status === 'idle' && ''}
            {status === 'ready' && !showProfileForm && '‚úÖ Camera ready ‚Äî enter profile below'}
            {status === 'capturing' && 'üì∏ Capturing selfie...'}
            {status === 'uploading' && '‚¨ÜÔ∏è  Verifying with AI...'}
            {status === 'error' && '‚ùå Error ‚Äî try again'}
            {showProfileForm && 'üë§ Set your anonymous profile'}
            {status === 'camera-error' && '‚ùå Camera access denied'}
          </div>

          <button
            className="btn"
            onClick={capture}
            disabled={status === 'capturing' || status === 'uploading'}
            style={{
              opacity: status === 'capturing' || status === 'uploading' ? 0.6 : 1,
              cursor: status === 'capturing' || status === 'uploading' ? 'not-allowed' : 'pointer',
            }}
          >
            {showProfileForm ? 'Save Profile & Capture' : 'Capture Selfie'}
          </button>
        </>
      )}
      {status === 'verified' && (
        <div style={{ textAlign: 'center', color: '#5eead4' }}>
          ‚úì Verified ‚Äî Redirecting to chat...
        </div>
      )}
    </div>
  )
}
