import React, { useEffect, useRef, useState } from 'react'
import './styles.css'

export default function Chat({ deviceId, profile }) {
  const [ws, setWs] = useState(null)
  const [state, setState] = useState('idle') // idle, queued, matched, chatting
  const [peer, setPeer] = useState(null)
  const [messages, setMessages] = useState([])
  const msgRef = useRef()

  useEffect(() => {
    const socket = new WebSocket(`ws://localhost:8000/ws?device_id=${deviceId}`)
    socket.onopen = () => {
      setWs(socket)
    }
    socket.onmessage = (e) => {
      const d = JSON.parse(e.data)
      if (d.type === 'queued') setState('queued')
      if (d.type === 'matched') {
        setPeer(d.peer)
        setState('chatting')
      }
      if (d.type === 'msg') {
        setMessages((m) => [...m, { from: d.from, text: d.text }])
      }
      if (d.type === 'peer_left') {
        setState('idle')
        setPeer(null)
      }
    }
    socket.onclose = () => setWs(null)
    return () => socket.close()
  }, [deviceId])

  function joinQueue(filter) {
    if (!ws) return
    ws.send(JSON.stringify({ action: 'join', filter, nickname: profile.nickname }))
  }

  function leave() {
    if (!ws) return
    ws.send(JSON.stringify({ action: 'leave' }))
    setState('idle')
    setPeer(null)
  }

  function sendMsg() {
    const text = msgRef.current.value
    if (!text || !ws) return
    ws.send(JSON.stringify({ action: 'msg', text }))
    setMessages((m) => [...m, { from: 'me', text }])
    msgRef.current.value = ''
  }

  function nextMatch() {
    if (!ws) return
    ws.send(JSON.stringify({ action: 'next' }))
    // re-join default any
    ws.send(JSON.stringify({ action: 'join', filter: 'any', nickname: profile.nickname }))
  }

  function report() {
    if (!ws || !peer) return
    ws.send(JSON.stringify({ action: 'report', reported: peer }))
    alert('Reported — thank you')
  }

  return (
    <div className="chat-root">
      <div className="sidebar">
        <div><strong>Nickname</strong>: {profile.nickname}</div>
        <div><strong>Bio</strong>: {profile.bio}</div>
        <div><strong>Gender</strong>: {profile.gender}</div>
        <div style={{ marginTop: 12 }}>
          <button onClick={() => joinQueue('any')}>Join Any</button>
          <button onClick={() => joinQueue('male')}>Join Male</button>
          <button onClick={() => joinQueue('female')}>Join Female</button>
        </div>
        <div style={{ marginTop: 12 }}>
          <button onClick={leave}>Leave</button>
        </div>
      </div>
      <div className="chat-area">
        <div className="chat-header">State: {state} {peer ? `(peer ${peer})` : ''}</div>
        <div className="messages">
          {messages.map((m, i) => (
            <div key={i} className={`msg ${m.from === 'me' ? 'me' : 'them'}`}>
              <div className="msg-from">{m.from === 'me' ? profile.nickname : 'Stranger'}</div>
              <div className="msg-text">{m.text}</div>
            </div>
          ))}
        </div>
        <div className="chat-controls">
          <input ref={msgRef} placeholder="Type a message" />
          <button onClick={sendMsg}>Send</button>
          <button onClick={nextMatch}>Next</button>
          <button onClick={report}>Report</button>
        </div>
      </div>
    </div>
  )
}
